import {
    SHOW_ALERT,
    SET_ALERT_MSG,
    SET_ALERT_TYPE
} from '../actions/types'


const initialState = {
    alert: false,
    msg: '',
    type: ''

}
export default function (state = initialState, action) {
    switch (action.type) {
        case SHOW_ALERT:
            return {
                ...state,
                alert: action.payload
            };
        case SET_ALERT_MSG:
            return {
                ...state,
                msg: action.payload
            };
        case SET_ALERT_TYPE:
            return {
                ...state,
                type: action.payload
            };
        default:
            return state;
    }
}
