import React, { useState } from 'react';
import { Form, Button, Col, Row, Container } from 'react-bootstrap';
import { connect } from 'react-redux';
import { createProfile } from '../../actions/createProfileAction';
import { initiateAlert } from '../../actions/alertAction';
import Alert from './Alert';

const ProfileForm = ({ createProfile, initiateAlert, alert }) => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [company, setCompany] = useState('');
  const [website, setWebsite] = useState('');
  const [location, setLocation] = useState('');
  const [skills, setSkills] = useState('');
  const [gitHub, setGithub] = useState('');
  const [bio, setBio] = useState('');
  const [linkedIn, setLinkedIn] = useState('');
  const [youtube, setYoutube] = useState('');

  const changeHandler = e => {
    if (e.target.value.length == 1) {
      e.target.classList.remove('is-invalid');
      e.target.className += ' is-valid';
    } else if (e.target.value.length == 0) {
      e.target.classList.remove('is-valid');
      e.target.className += ' is-invalid';
    }
  };
  console.log(alert);
  return (
    <Container>
      <Alert alert={alert} />
      <Form
        className='mx-5'
        onSubmit={event => {
          const profile = {
            firstName,
            lastName,
            email,
            company,
            website,
            location,
            skills,
            gitHub,
            bio,
            linkedIn,
            youtube,
          };
          let bool = true;
          for (const key of Object.values(profile)) {
            console.log(key);
            if (!key) {
              bool = false;
              /*  initiateAlert(
                event,
                true,
                'Please fill out all Fields!',
                'warning'
              ); */
            }
          }
          if (!bool) {
            initiateAlert(
              event,
              true,
              'Please fill out all Fields!',
              'warning'
            );
            setTimeout(() => initiateAlert(event, false, '', ''), 5000);
          }
          console.log(bool);
          createProfile(event, profile);
        }}
      >
        <Form.Row>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Firstname</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              placeholder='Enter your Firstname'
              onChange={event => {
                changeHandler(event);
                setFirstName(event.target.value);
              }}
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Lastname</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              placeholder='Enter your Lastname'
              onChange={event => {
                changeHandler(event);
                setLastName(event.target.value);
              }}
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
        </Form.Row>
        <Form.Row>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Email</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setEmail(event.target.value);
              }}
              placeholder='Enter your Email'
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Company</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setCompany(event.target.value);
              }}
              placeholder="Enter your Company's Name"
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
        </Form.Row>
        <Form.Row>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Website</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setWebsite(event.target.value);
              }}
              placeholder="Enter your website's Adress"
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Location</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setLocation(event.target.value);
              }}
