import React, { useState } from 'react';
import { Form, Button, Col, Row, Container } from 'react-bootstrap';
import { connect } from 'react-redux';
import { createProfile } from '../../actions/createProfileAction';

const ProfileForm = ({ createProfile }) => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [company, setCompany] = useState('');
  const [website, setWebsite] = useState('');
  const [location, setLocation] = useState('');
  const [skills, setSkills] = useState('');
  const [gitHub, setGithub] = useState('');
  const [bio, setBio] = useState('');
  const [linkedIn, setLinkedIn] = useState('');
  const [youtube, setYoutube] = useState('');

  const changeHandler = e => {
    if (e.target.value.length == 1) {
      e.target.classList.remove('is-invalid');
      e.target.className += ' is-valid';
    } else if (e.target.value.length == 0) {
      e.target.classList.remove('is-valid');
      e.target.className += ' is-invalid';
    }
  };
  return (
    <Container>
      <Form
        className='mx-5'
        onSubmit={event =>
          createProfile(event, {
            firstName,
            lastName,
            email,
            company,
            website,
            location,
            skills,
            gitHub,
            bio,
            linkedIn,
            youtube,
          })
        }
      >
        <Form.Row>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Firstname</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              placeholder='Enter your Firstname'
              onChange={event => {
                changeHandler(event);
                setFirstName(event.target.value);
              }}
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Lastname</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              placeholder='Enter your Lastname'
              onChange={event => {
                changeHandler(event);
                setLastName(event.target.value);
              }}
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
        </Form.Row>
        <Form.Row>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Email</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setEmail(event.target.value);
              }}
              placeholder='Enter your Email'
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Company</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setCompany(event.target.value);
              }}
              placeholder="Enter your Company's Name"
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
        </Form.Row>
        <Form.Row>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Website</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setWebsite(event.target.value);
              }}
              placeholder="Enter your website's Adress"
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Location</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setLocation(event.target.value);
              }}
              placeholder='1234 Main St'
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>
        </Form.Row>

        <Form.Group className='mx-5'>
          <Form.Label>Skills</Form.Label>
          <Form.Control
            isInvalid
            type='text'
            size='lg'
            onChange={event => {
              changeHandler(event);
              setSkills(event.target.value);
            }}
            placeholder='Enter your skills'
          />
          <Form.Control.Feedback type='valid'>
            You did it!
          </Form.Control.Feedback>
          <Form.Control.Feedback type='invalid'>
            Please fill out this field
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Row>
          <Form.Group className='mx-5' as={Col}>
            <Form.Label>GitHub Username</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setGithub(event.target.value);
              }}
              placeholder="Enter your GitHub's Username"
            />
            <Form.Control.Feedback type='valid'>
              You did it!
            </Form.Control.Feedback>
            <Form.Control.Feedback type='invalid'>
              Please fill out this field
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className='mx-5' as={Col}>
            <Form.Label>Bio</Form.Label>
            <Form.Control
              isInvalid
              type='text'
              size='lg'
              onChange={event => {
                changeHandler(event);
                setBio(event.target.value);
              }}
              placeholder='Enter your Bio'
            />
