import {
    SET_ALERT
} from './types'

export const initiateAlert = (e, alert, msg, type) => dispatch => {
    e.preventDefault()
    const alertObj = {
        alert,
        msg,
        type
    }
    dispatch({
        type: SET_ALERT,
        payload: alertObj
    })
    /*   dispatch({
          type: SHOW_ALERT,
          payload: alert
      })
      dispatch({
          type: SET_ALERT_MSG,
          payload: msg
      })
      dispatch({
          type: SET_ALERT_TYPE,
          payload: typ
      }) */
}
