import {
    SHOW_ALERT,
    SET_ALERT_MSG,
    SET_ALERT_TYPE
} from './types'

export const initiateAlert = (e, alert, msg, typ) => dispatch => {
    e.preventDefault()

    dispatch({
        type: SHOW_ALERT,
        payload: alert
    })
    dispatch({
        type: SET_ALERT_MSG,
        payload: msg
    })
    dispatch({
        type: SET_ALERT_TYPE,
        payload: typ
    })
}
