import {
    SET_ALERT
} from '../actions/types'


const initialState = {
    alertObj: null

}
export default function (state = initialState, action) {
    switch (action.type) {
        case SHOW_ALERT:
            return {
                ...state,
                alertObj: action.payload
            };

        default:
            return state;
    }
}
